package com.app.smartscanimport android.content.Intentimport androidx.compose.ui.test.junit4.createAndroidComposeRuleimport androidx.compose.ui.test.onNodeWithTextimport androidx.compose.ui.test.performClickimport androidx.test.core.app.ApplicationProviderimport org.junit.Ruleimport org.junit.Testimport org.junit.runner.RunWithimport androidx.test.ext.junit.runners.AndroidJUnit4import org.junit.Assert.assertTrue/** * Instrumented UI tests for MainActivity. * * These tests verify that the correct navigation behavior occurs * when each analysis mode button is clicked. * * Each test simulates a user tapping a button and checks that * the expected Intent configuration is correctly set. */@RunWith(AndroidJUnit4::class)class MainActivityTest {    @get:Rule    val composeRule = createAndroidComposeRule<MainActivity>()    /**     * Test 1:     * Simulates a click on "Skin Analyzer" and verifies     * that an Intent for SkinAnalyzerActivity is created.     */    @Test    fun clickSkinAnalyzer_startsSkinAnalyzerActivity() {        composeRule.onNodeWithText("Skin Analyzer").performClick()        val context = ApplicationProvider.getApplicationContext<android.content.Context>()        val expectedIntent = Intent(context, com.app.smartscan.analysis.SkinAnalyzerActivity::class.java)        assertTrue(expectedIntent.component?.className?.contains("SkinAnalyzerActivity") == true)    }    /**     * Test 2:     * Simulates a click on "OCR Text Reader" and verifies     * that the Intent includes the correct analysis type.     */    @Test    fun clickOcrReader_startsAiCameraActivity_withOcrType() {        composeRule.onNodeWithText("OCR Text Reader").performClick()        val context = ApplicationProvider.getApplicationContext<android.content.Context>()        val expectedIntent = Intent(context, com.app.smartscan.aiCamera.AiCameraActivity::class.java).apply {            putExtra("analysis_type", "ocr")        }        assertTrue(expectedIntent.hasExtra("analysis_type"))        assertTrue(expectedIntent.getStringExtra("analysis_type") == "ocr")    }    /**     * Test 3:     * Simulates a click on "Barcode Scanner" and verifies     * that the Intent includes the correct analysis type.     */    @Test    fun clickBarcodeScanner_startsAiCameraActivity_withBarcodeType() {        composeRule.onNodeWithText("Barcode Scanner").performClick()        val context = ApplicationProvider.getApplicationContext<android.content.Context>()        val expectedIntent = Intent(context, com.app.smartscan.aiCamera.AiCameraActivity::class.java).apply {            putExtra("analysis_type", "barcode")        }        assertTrue(expectedIntent.hasExtra("analysis_type"))        assertTrue(expectedIntent.getStringExtra("analysis_type") == "barcode")    }}